import React, { useState, useEffect } from 'react';
import { Table, Form, Button, Alert } from '@/components/ui/';

const CampList = () => {
  const [campgrounds, setCampgrounds] = useState([]);
  const [newCampground, setNewCampground] = useState({
    latitude: '',
    longitude: '',
    code: '',
    name: '',
    type: '',
    phone: '',
    dates_open: '',
    comments: '',
    num_sites: '',
    elevation: '',
    ammenities: '',
    state: '',
    nearest_town_distance: '',
    nearest_town_bearing: '',
    city: ''
  });
  const [error, setError] = useState('');

  useEffect(() => {
    fetchCampgrounds();
  }, []);

  const fetchCampgrounds = async () => {
    try {
      const response = await fetch('/api/campgrounds');
      if (!response.ok) throw new Error('Failed to fetch campgrounds');
      const data = await response.json();
      setCampgrounds(data);
    } catch (err) {
      setError('Failed to load campgrounds');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCampground(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/campgrounds/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newCampground)
      });
      if (!response.ok) throw new Error('Failed to add campground');
      fetchCampgrounds();
      setNewCampground({
        latitude: '',
        longitude: '',
        code: '',
        name: '',
        type: '',
        phone: '',
        dates_open: '',
        comments: '',
        num_sites: '',
        elevation: '',
        ammenities: '',
        state: '',
        nearest_town_distance: '',
        nearest_town_bearing: '',
        city: ''
      });
    } catch (err) {
      setError('Failed to add campground');
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Campground Management</h1>
      
      {error && <Alert variant="destructive">{error}</Alert>}
      
      <Form onSubmit={handleSubmit} className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Add New Campground</h2>
        {Object.keys(newCampground).map((key) => (
          <Form.Field key={key}>
            <Form.Label htmlFor={key}>{key.replace('_', ' ').toUpperCase()}</Form.Label>
            <Form.Input
              type="text"
              id={key}
              name={key}
              value={newCampground[key]}
              onChange={handleInputChange}
              required
            />
          </Form.Field>
        ))}
        <Button type="submit">Add Campground</Button>
      </Form>
      
      <h2 className="text-xl font-semibold mb-2">Campgrounds List</h2>
      <Table>
        <Table.Header>
          <Table.Row>
            <Table.Head>Name</Table.Head>
            <Table.Head>Type</Table.Head>
            <Table.Head>State</Table.Head>
            <Table.Head>Actions</Table.Head>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {campgrounds.map((campground) => (
            <Table.Row key={campground.id}>
              <Table.Cell>{campground.name}</Table.Cell>
              <Table.Cell>{campground.type}</Table.Cell>
              <Table.Cell>{campground.state}</Table.Cell>
              <Table.Cell>
                <Button variant="outline" size="sm" className="mr-2">
                  Edit
                </Button>
                <Button variant="destructive" size="sm">
                  Delete
                </Button>
              </Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    </div>
  );
};

export default CampList;
